const chalk = require('chalk');
const webpack = require('webpack');

const argv = require('../utils/yargs');

const mode = argv.typescript ? 'typescript' : 'javascript';

const webpackConfig = require('../webpack')[mode].prod();

const compiler = webpack(webpackConfig);

const { log } = console;

compiler.run((err, stats) => {
  if (err || stats.hasErrors()) {
    log(chalk.red('Failed to compile with the following errors:\n'));

    const statsJson = stats.toJson('verbose');
    if (argv.verbose) {
      log(statsJson);
    } else {
      statsJson.errors.forEach((error) => {
        log(error);
        log();
      });
    }
    process.exit(1);
  }

  log(chalk.green('Build was successful'));
});
